DeviceProperties.ReadBrightnessScriptPacketParser:
INT value;

VAR mode := PARSEJSON(jsonStatus, "$.lights[0].mode");

IF mode = "white"
    value := PARSEJSON(jsonStatus, "$.lights[0].brightness", 1);
ELSE
    value := PARSEJSON(jsonStatus, "$.lights[0].gain", 1);
END

IF ISNULL(value)
    RETURN(NaN);
END

BOOL isOn := PARSEJSON(jsonStatus, "$.lights[0].ison", 1);

IF ISNULL(isOn)
    RETURN(NaN);
END

IF(isOn, value/100.0, 0);

DeviceProperties.ReadCCTScriptPacketParser:
INT value := PARSEJSON(jsonStatus, "$.lights[0].temp", 1);
STRING mode := PARSEJSON(jsonStatus, "$.lights[0].mode", 1);

IF ISNULL(value) OR mode != "white"
    RETURN(NaN);
END

value

DeviceProperties.WriteBrightnessScriptPacketParser:
IF St = 1
    VAR mode := PARSEJSON(jsonStatus, "$.lights[0].mode");
    IF mode = "white"
        SENDHTTPREQUEST("light/0?turn=on&brightness=" + ROUND(Hb*100.0));
    ELSE
        SENDHTTPREQUEST("light/0?turn=on&gain=" + ROUND(Hb*100.0));
    END
ELSEIF St = 0
    SENDHTTPREQUEST("light/0?turn=off");
END

DeviceProperties.WriteCCTScriptPacketParser:
IF Ct = NaN
    RETURN(0);
END

VAR temp := MIN(Ct, 6500);
temp := MAX(temp, 3000);

SENDHTTPREQUEST("light/0?mode=white&temp=" + temp);
